    connection = get_connection()
    cursor = connection.cursor() 
    n = cursor.callfunc(name="get_SPA",returnType=str,parameters =[year, term, stu_id])
    spa6_en.delete(0,"end")
    spa6_en.insert(0,n)
    connection.commit()
